import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  email: string = '';
  file: File = new File([], "null");
  errorMessage: string = '';

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {
    // Injecting HttpClient in the constructor
  }

  onFileSelected() {
    const inputNode: any = document.querySelector('#file');
    this.file = inputNode.files[0] as File;
  }

  uploadFile(): void {
    const formData = new FormData();
    formData.append('email', this.email);
    formData.append('file', this.file);

    //this.http.post(`${this.baseUrl}/file/upload`, formData)
    this.http.post('https://localhost:7182/file/upload', formData)
      .subscribe(
        (response: any) => {
          // Reset error message
          alert(response.message);
          // Alert with success message
        },
        (error: any) => {
          console.error(error);
          // Display detailed error message from IActionResult response
          this.errorMessage = error.error.title || error.error.message;
          alert(this.errorMessage);
        }
      );

  }
}
<div class="container">
  <br>
  <input type="email" placeholder="Enter Email" [(ngModel)]="email" />
  <br>
  <button type="button" mat-stroked-button (click)="fileInput.click()">Upload File</button>
  <br>
  <input hidden (change)="onFileSelected()" #fileInput type="file" id="file">
  <br>
  <br>
  <button type="submit" mat-button (click)="uploadFile()">Send</button>
</div>

<style>
  .container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh; /* Adjust as needed to center vertically in the viewport */
  }

  /* Add additional styling as needed */

</style>
