// app.component.ts
import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  email: string = ''; 

  constructor(private http: HttpClient) {
    // Injecting HttpClient in the constructor
  }

  onFileSelected() {
    const inputNode: any = document.querySelector('#file');
    const file = inputNode.files[0];

    // Ensure a file is selected
    if (file) {
      this.uploadFile(this.email, file);
    } else {
      console.error('No file selected.');
    }
  }

  uploadFile(email: string, file: File): void {
    const formData = new FormData();
    formData.append('email', email);
    formData.append('file', file);

    this.http.post('https://localhost/upload', formData)
      .subscribe(
        response => {
          console.log('File upload successful:', response);
          // Handle success as needed
        },
        error => {
          console.error('Error uploading file:', error);
          // Handle error as needed
        }
      );
  }
}
<div>
  <br>
  <input type="email" placeholder="Enter Email" [(ngModel)]="email" />
  <br>
  <button type="button" mat-stroked-button (click)="fileInput.click()">Upload File</button>
  <input hidden (change)="onFileSelected()" #fileInput type="file" id="file">
</div>
